// App.tsx
import React, { useEffect, useState } from "react";
import axios from "axios";

interface User {
    id?: number;
    address: string;
    email: string;
    username: string;
    password: string;
}

interface Product {
    id: number;
    name: string;
    price: number;
    quantity: number;
}

interface CartItem {
    id: number;
    product: Product;
    quantity: number;
}

function App() {
    const [userId, setUserId] = useState("");
    const [users, setUsers] = useState<User[]>([]);
    const [products, setProducts] = useState<Product[]>([]);
    const [cartItems, setCartItems] = useState<CartItem[]>([]);
    const [newUser, setNewUser] = useState<User>({
        username: "",
        email: "",
        address: "",
        password: ""
    });
    const [editUserId, setEditUserId] = useState<number | null>(null);

    const loadUsers = async () => {
        const res = await axios.get("http://localhost:8080/api/users");
        setUsers(res.data);
    };

    const loadProducts = async () => {
        const res = await axios.get("http://localhost:8080/api/products");
        setProducts(res.data);
    };

    const loadCartItems = async () => {
        if (!userId) return;
        const res = await axios.get(`http://localhost:8080/api/cart/user/${userId}`);
        setCartItems(res.data);
    };

    useEffect(() => {
        loadUsers();
        loadProducts();
    }, []);

    useEffect(() => {
        if (userId) {
            loadCartItems();
        }
    }, [userId]);

    const handleCreateUser = async () => {
        try {
            await axios.post("/api/users", newUser);
            setNewUser({ username: "", email: "", address: "", password: "" });
            loadUsers();
        } catch {
            alert("Error creating user");
        }
    };

    const handleUpdateUser = async () => {
        if (editUserId === null) return;
        try {
            await axios.put(`/api/users/${editUserId}`, newUser);
            setEditUserId(null);
            setNewUser({ username: "", email: "", address: "", password: "" });
            loadUsers();
        } catch {
            alert("Error updating user");
        }
    };

    const handleEdit = (user: User) => {
        setEditUserId(user.id!);
        setNewUser(user);
    };

    const handleDeleteUser = async (id?: number) => {
        try {
            await axios.delete(`/api/users/${id}`);
            loadUsers();
        } catch {
            alert("Error deleting user");
        }
    };

    const handleAddToCart = async (productId: number) => {
        if (!userId) {
            alert("Please select a user first.");
            return;
        }
        try {
            await axios.post(`http://localhost:8080/api/cart/add`, null, {
                params: { userId, productId, quantity: 1 }
            });
            loadCartItems();
        } catch (error) {
            alert("Error adding to cart");
        }
    };

    const handleRemoveFromCart = async (cartItemId: number) => {
        await axios.delete(`http://localhost:8080/api/cart/${cartItemId}`);
        loadCartItems();
    };

    const handleUpdateCartQuantity = async (cartItemId: number, quantity: number) => {
        await axios.put(`http://localhost:8080/api/cart/update/${cartItemId}?newQuantity=${quantity}`);
        loadCartItems();
    };

    const containerStyle = {
        fontFamily: "Arial, sans-serif",
        backgroundColor: "#f0f4f8",
        minHeight: "100vh",
        padding: "20px",
        color: "#333"
    };

    const cardStyle = {
        backgroundColor: "#fff",
        padding: "20px",
        borderRadius: "10px",
        marginBottom: "20px",
        boxShadow: "0 4px 8px rgba(0,0,0,0.1)"
    };

    const inputStyle = {
        padding: "10px",
        margin: "5px 0",
        width: "100%",
        borderRadius: "5px",
        border: "1px solid #ccc"
    };

    const buttonStyle = {
        marginRight: "10px",
        padding: "10px 15px",
        border: "none",
        borderRadius: "5px",
        backgroundColor: "#007bff",
        color: "white",
        cursor: "pointer"
    };

    const dangerButton = {
        ...buttonStyle,
        backgroundColor: "#dc3545"
    };

    const editButton = {
        ...buttonStyle,
        backgroundColor: "#ffc107",
        color: "#333"
    };

    return (
        <div style={containerStyle}>
            <h1 style={{ color: "#007bff" }}>E-commerce App</h1>

            <div style={cardStyle}>
                <h2>{editUserId ? "Edit User" : "Create User"}</h2>
                <input style={inputStyle} placeholder="Username" value={newUser.username} onChange={(e) => setNewUser({ ...newUser, username: e.target.value })} />
                <input style={inputStyle} placeholder="Email" value={newUser.email} onChange={(e) => setNewUser({ ...newUser, email: e.target.value })} />
                <input style={inputStyle} placeholder="Address" value={newUser.address} onChange={(e) => setNewUser({ ...newUser, address: e.target.value })} />
                <input type="password" style={inputStyle} placeholder="Password" value={newUser.password} onChange={(e) => setNewUser({ ...newUser, password: e.target.value })} />
                {editUserId ? (
                    <button style={editButton} onClick={handleUpdateUser}>Update User</button>
                ) : (
                    <button style={buttonStyle} onClick={handleCreateUser}>Create User</button>
                )}
            </div>

            <div style={cardStyle}>
                <h2>All Users</h2>
                <ul>
                    {users.map((u) => (
                        <li key={u.id} style={{ marginBottom: "10px" }}>
                            <strong>{u.username}</strong> ({u.email})<br />
                            <button style={buttonStyle} onClick={() => setUserId(u.id?.toString() || "")}>Use</button>
                            <button style={editButton} onClick={() => handleEdit(u)}>Edit</button>
                            <button style={dangerButton} onClick={() => handleDeleteUser(u.id)}>Delete</button>
                        </li>
                    ))}
                </ul>
            </div>

            <div style={cardStyle}>
                <h2>Products</h2>
                <ul>
                    {products.map((p) => (
                        <li key={p.id} style={{ marginBottom: "10px" }}>
                            {p.name} - ${p.price} ({p.quantity} in stock)
                            <button style={buttonStyle} onClick={() => handleAddToCart(p.id)}>Add to Cart</button>
                        </li>
                    ))}
                </ul>
            </div>

            <div style={cardStyle}>
                <h2>Cart (User ID: {userId || "None"})</h2>
                {cartItems.length === 0 ? (
                    <p>No items in cart.</p>
                ) : (
                    <ul>
                        {cartItems.map((item) => (
                            <li key={item.id}>
                                {item.product.name} - Qty:
                                <input
                                    type="number"
                                    value={item.quantity}
                                    min={1}
                                    style={{ width: "50px", margin: "0 10px" }}
                                    onChange={(e) => handleUpdateCartQuantity(item.id, parseInt(e.target.value))}
                                />
                                <button style={dangerButton} onClick={() => handleRemoveFromCart(item.id)}>Remove</button>
                            </li>
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
}

export default App;
