import React, { useEffect, useState } from "react";
import axios from "axios";

interface User {
    id?: number;
    address: string;
    email: string;
    username: string;
    password: string;
}

interface Product {
    id: number;
    name: string;
    description: string;
    price: number;
    quantity: number;
}

interface CartItem {
    id: number;
    product: Product;
    quantity: number;
}

function App() {
    const [userId, setUserId] = useState("");
    const [users, setUsers] = useState<User[]>([]);
    const [newUser, setNewUser] = useState<User>({
        username: "",
        email: "",
        address: "",
        password: ""
    });
    const [editUserId, setEditUserId] = useState<number | null>(null);
    const [products, setProducts] = useState<Product[]>([]);
    const [cart, setCart] = useState<CartItem[]>([]);
    const [selectedProductId, setSelectedProductId] = useState("");
    const [selectedQuantity, setSelectedQuantity] = useState(1);

    const loadUsers = async () => {
        const res = await axios.get("http://localhost:8080/api/users");
        setUsers(res.data);
    };

    const loadProducts = async () => {
        const res = await axios.get("http://localhost:8080/api/products");
        setProducts(res.data);
    };

    const loadCart = async () => {
        if (!userId) return;
        const res = await axios.get(`http://localhost:8080/api/cart/user/${userId}`);
        setCart(res.data);
    };

    useEffect(() => {
        loadUsers();
        loadProducts();
    }, []);

    useEffect(() => {
        loadCart();
    }, [userId]);

    const handleCreateUser = async () => {
        try {
            await axios.post("/api/users", newUser);
            setNewUser({ username: "", email: "", address: "", password: "" });
            loadUsers();
        } catch {
            alert("Error creating user");
        }
    };

    const handleUpdateUser = async () => {
        if (editUserId === null) return;
        try {
            await axios.put(`/api/users/${editUserId}`, newUser);
            setEditUserId(null);
            setNewUser({ username: "", email: "", address: "", password: "" });
            loadUsers();
        } catch {
            alert("Error updating user");
        }
    };

    const handleEdit = (user: User) => {
        setEditUserId(user.id!);
        setNewUser(user);
    };

    const handleDeleteUser = async (id?: number) => {
        try {
            await axios.delete(`/api/users/${id}`);
            loadUsers();
        } catch {
            alert("Error deleting user");
        }
    };

    const handleAddToCart = async () => {
        if (!userId || !selectedProductId) return;
        try {
            await axios.post(`http://localhost:8080/api/cart/add`, null, {
                params: {
                    userId,
                    productId: selectedProductId,
                    quantity: selectedQuantity
                }
            });
            loadCart();
        } catch {
            alert("Error adding to cart");
        }
    };

    const handleRemoveFromCart = async (cartItemId: number) => {
        try {
            await axios.delete(`http://localhost:8080/api/cart/${cartItemId}`);
            loadCart();
        } catch {
            alert("Error removing item from cart");
        }
    };

    const handleUpdateCartQuantity = async (cartItemId: number, newQuantity: number) => {
        try {
            await axios.put(`http://localhost:8080/api/cart/update/${cartItemId}`, null, {
                params: { newQuantity }
            });
            loadCart();
        } catch {
            alert("Error updating cart quantity");
        }
    };

    const inputStyle = {
        padding: "10px",
        margin: "5px 0",
        width: "100%",
        borderRadius: "5px",
        border: "1px solid #ccc"
    };

    const buttonStyle = {
        marginRight: "10px",
        padding: "10px 15px",
        border: "none",
        borderRadius: "5px",
        backgroundColor: "#007bff",
        color: "white",
        cursor: "pointer"
    };

    const cardStyle = {
        backgroundColor: "#fff",
        padding: "20px",
        borderRadius: "10px",
        marginBottom: "20px",
        boxShadow: "0 4px 8px rgba(0,0,0,0.1)"
    };

    return (
        <div style={{ padding: "20px", fontFamily: "Arial" }}>
            <h1>Cart App</h1>

            <div style={cardStyle}>
                <h2>{editUserId ? "Edit User" : "Create User"}</h2>
                <input style={inputStyle} placeholder="Username" value={newUser.username} onChange={(e) => setNewUser({ ...newUser, username: e.target.value })} />
                <input style={inputStyle} placeholder="Email" value={newUser.email} onChange={(e) => setNewUser({ ...newUser, email: e.target.value })} />
                <input style={inputStyle} placeholder="Address" value={newUser.address} onChange={(e) => setNewUser({ ...newUser, address: e.target.value })} />
                <input type="password" style={inputStyle} placeholder="Password" value={newUser.password} onChange={(e) => setNewUser({ ...newUser, password: e.target.value })} />
                <button style={buttonStyle} onClick={editUserId ? handleUpdateUser : handleCreateUser}>
                    {editUserId ? "Update" : "Create"}
                </button>
            </div>

            <div style={cardStyle}>
                <h2>Users</h2>
                {users.map((u) => (
                    <div key={u.id}>
                        <b>{u.username}</b> - {u.email}
                        <br />
                        <button style={buttonStyle} onClick={() => setUserId(u.id?.toString() || "")}>Use</button>
                        <button style={{ ...buttonStyle, backgroundColor: "#ffc107", color: "#333" }} onClick={() => handleEdit(u)}>Edit</button>
                        <button style={{ ...buttonStyle, backgroundColor: "#dc3545" }} onClick={() => handleDeleteUser(u.id)}>Delete</button>
                        <hr />
                    </div>
                ))}
            </div>

            {userId && (
                <>
                    <div style={cardStyle}>
                        <h2>Add to Cart</h2>
                        <select style={inputStyle} value={selectedProductId} onChange={(e) => setSelectedProductId(e.target.value)}>
                            <option value="">-- Select Product --</option>
                            {products.map((p) => (
                                <option key={p.id} value={p.id}>
                                    {p.name} - ${p.price} - Stock: {p.quantity}
                                </option>
                            ))}
                        </select>
                        <input
                            type="number"
                            min="1"
                            value={selectedQuantity}
                            onChange={(e) => setSelectedQuantity(parseInt(e.target.value))}
                            style={inputStyle}
                            placeholder="Quantity"
                        />
                        <button style={buttonStyle} onClick={handleAddToCart}>Add</button>
                    </div>

                    <div style={cardStyle}>
                        <h2>User Cart</h2>
                        {cart.length === 0 ? <p>No items in cart</p> : (
                            <ul>
                                {cart.map((item) => (
                                    <li key={item.id}>
                                        {item.product.name} - Qty: {item.quantity}
                                        <button
                                            style={{ ...buttonStyle, backgroundColor: "#28a745" }}
                                            onClick={() => handleUpdateCartQuantity(item.id, item.quantity + 1)}
                                        >+</button>
                                        <button
                                            style={{ ...buttonStyle, backgroundColor: "#ffc107", color: "#333" }}
                                            onClick={() => item.quantity > 1 && handleUpdateCartQuantity(item.id, item.quantity - 1)}
                                        >-</button>
                                        <button
                                            style={{ ...buttonStyle, backgroundColor: "#dc3545" }}
                                            onClick={() => handleRemoveFromCart(item.id)}
                                        >Remove</button>
                                    </li>
                                ))}
                            </ul>
                        )}
                    </div>
                </>
            )}
        </div>
    );
}

export default App;
