import React, { useEffect, useState } from "react";
import axios from "axios";

interface User {
    id?: number;
    address: string;
    email: string;
    username: string;
    password: string;
}

interface Product {
    id?: number;
    name: string;
    description: string;
    price: number;
    stock: number;
    quantity: number;
}
interface CartItem {
    id: number;
    product: Product;
    quantity: number;
}
const [cartItems, setCartItems] = useState<CartItem[]>([]);
const loadCart = async () => {
    if (!userId) return;
    try {
        const res = await axios.get(`http://localhost:8080/api/cart/user/${userId}`);
        setCartItems(res.data);
    } catch {
        alert("Error loading cart");
    }
};


function App() {
    const [userId, setUserId] = useState("");
    const [purchases, setPurchases] = useState([]);
    const [users, setUsers] = useState<User[]>([]);
    const [newUser, setNewUser] = useState<User>({
        username: "",
        email: "",
        address: "",
        password: ""
    });
    const [editUserId, setEditUserId] = useState<number | null>(null);

    const [products, setProducts] = useState<Product[]>([]);
    const [newProduct, setNewProduct] = useState<Product>({
        name: "",
        description: "",
        price: 0,
        stock: 0,
        quantity: 0
    });
    const [editProductId, setEditProductId] = useState<number | null>(null);

    const loadUsers = async () => {
        const res = await axios.get("http://localhost:8080/api/users");
        setUsers(res.data);
    };

    const loadProducts = async () => {
        const res = await axios.get("http://localhost:8080/products");
        setProducts(res.data);
    };

    useEffect(() => {
        loadUsers();
        loadProducts();
    }, []);

    const handleCreateUser = async () => {
        try {
            await axios.post("/api/users", newUser);
            setNewUser({ username: "", email: "", address: "", password: "" });
            loadUsers();
        } catch {
            alert("Error creating user");
        }
    };

    const handleUpdateUser = async () => {
        if (editUserId === null) return;
        try {
            await axios.put(`/api/users/${editUserId}`, newUser);
            setEditUserId(null);
            setNewUser({ username: "", email: "", address: "", password: "" });
            loadUsers();
        } catch {
            alert("Error updating user");
        }
    };

    const handleEdit = (user: User) => {
        setEditUserId(user.id!);
        setNewUser(user);
    };

    const handleDeleteUser = async (id?: number) => {
        try {
            await axios.delete(`/api/users/${id}`);
            loadUsers();
        } catch {
            alert("Error deleting user");
        }
    };

    const handleBuy = async () => {
        try {
            const response = await axios.post(`http://localhost:8080/api/purchases/buy?userId=${userId}`);
            alert("Purchase created with ID: " + response.data.id);
        } catch (error) {
            alert("Error creating purchase");
        }
    };

    const handleGetPurchases = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/api/purchases/user/${userId}`);
            setPurchases(response.data);
        } catch (error) {
            alert("Error fetching purchases");
        }
    };

    const handleCreateProduct = async () => {
        try {
            await axios.post("http://localhost:8080/products", newProduct);
            setNewProduct({ name: "", description: "", price: 0, stock: 0, quantity: 0 });
            loadProducts();
        } catch {
            alert("Error creating product");
        }
    };

    const handleUpdateProduct = async () => {
        if (editProductId === null) return;
        try {
            await axios.put(`http://localhost:8080/products/${editProductId}`, newProduct);
            setEditProductId(null);
            setNewProduct({ name: "", description: "", price: 0, stock: 0, quantity: 0 });
            loadProducts();
        } catch {
            alert("Error updating product");
        }
    };

    const handleEditProduct = (product: Product) => {
        setEditProductId(product.id!);
        setNewProduct(product);
    };

    const handleDeleteProduct = async (id?: number) => {
        try {
            await axios.delete(`http://localhost:8080/products/${id}`);
            loadProducts();
        } catch {
            alert("Error deleting product");
        }
    };

    const containerStyle = {
        fontFamily: "Arial, sans-serif",
        backgroundColor: "#f0f4f8",
        minHeight: "100vh",
        padding: "20px",
        color: "#333"
    };

    const cardStyle = {
        backgroundColor: "#fff",
        padding: "20px",
        borderRadius: "10px",
        marginBottom: "20px",
        boxShadow: "0 4px 8px rgba(0,0,0,0.1)"
    };

    const inputStyle = {
        padding: "10px",
        margin: "5px 0",
        width: "100%",
        borderRadius: "5px",
        border: "1px solid #ccc"
    };

    const buttonStyle = {
        marginRight: "10px",
        padding: "10px 15px",
        border: "none",
        borderRadius: "5px",
        backgroundColor: "#007bff",
        color: "white",
        cursor: "pointer"
    };

    const dangerButton = {
        ...buttonStyle,
        backgroundColor: "#dc3545"
    };

    const editButton = {
        ...buttonStyle,
        backgroundColor: "#ffc107",
        color: "#333"
    };

    return (
        <div style={containerStyle}>
            <h1 style={{ color: "#007bff" }}>Purchases App</h1>

            <div style={cardStyle}>
                <h2>{editUserId ? "Edit User" : "Create User"}</h2>
                <input style={inputStyle} placeholder="Username" value={newUser.username} onChange={(e) => setNewUser({ ...newUser, username: e.target.value })} />
                <input style={inputStyle} placeholder="Email" value={newUser.email} onChange={(e) => setNewUser({ ...newUser, email: e.target.value })} />
                <input style={inputStyle} placeholder="Address" value={newUser.address} onChange={(e) => setNewUser({ ...newUser, address: e.target.value })} />
                <input type="password" style={inputStyle} placeholder="Password" value={newUser.password} onChange={(e) => setNewUser({ ...newUser, password: e.target.value })} />
                {editUserId ? (
                    <button style={editButton} onClick={handleUpdateUser}>Update User</button>
                ) : (
                    <button style={buttonStyle} onClick={handleCreateUser}>Create User</button>
                )}
            </div>

            <div style={cardStyle}>
                <h2>All Users</h2>
                <ul>
                    {users.map((u) => (
                        <li key={u.id} style={{ marginBottom: "10px" }}>
                            <strong>{u.username}</strong> ({u.email})<br />
                            <button style={buttonStyle} onClick={() => setUserId(u.id?.toString() || "")}>Use</button>
                            <button style={editButton} onClick={() => handleEdit(u)}>Edit</button>
                            <button style={dangerButton} onClick={() => handleDeleteUser(u.id)}>Delete</button>
                        </li>
                    ))}
                </ul>
            </div>


            <div style={cardStyle}>
                <h2>{editProductId ? "Edit Product" : "Create Product"}</h2>
                <input style={inputStyle} placeholder="Name" value={newProduct.name} onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })} />
                <input style={inputStyle} placeholder="Description" value={newProduct.description} onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })} />
                <input style={inputStyle} type="number" placeholder="Price" value={newProduct.price} onChange={(e) => setNewProduct({ ...newProduct, price: parseFloat(e.target.value) })} />
                <input style={inputStyle} type="number" placeholder="Stock" value={newProduct.stock} onChange={(e) => setNewProduct({ ...newProduct, stock: parseInt(e.target.value) })} />
                <input style={inputStyle} type="number" placeholder="Quantity" value={newProduct.quantity} onChange={(e) => setNewProduct({ ...newProduct, quantity: parseInt(e.target.value) })} />
                {editProductId ? (
                    <button style={editButton} onClick={handleUpdateProduct}>Update Product</button>
                ) : (
                    <button style={buttonStyle} onClick={handleCreateProduct}>Create Product</button>
                )}
            </div>

            <div style={cardStyle}>
                <h2>All Products</h2>
                <ul>
                    {products.map((p) => (
                        <li key={p.id} style={{ marginBottom: "10px" }}>
                            <strong>{p.name}</strong> - {p.description}<br />
                            Price: {p.price} | Stock: {p.stock} | Qty: {p.quantity}<br />
                            <button style={editButton} onClick={() => handleEditProduct(p)}>Edit</button>
                            <button style={dangerButton} onClick={() => handleDeleteProduct(p.id)}>Delete</button>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default App;
