import React, { useState } from "react";
import axios from "axios";

function App() {
  const [userId, setUserId] = useState("");
  const [purchases, setPurchases] = useState([]);

  const handleBuy = async () => {
    try {
      const response = await axios.post(`/api/purchases/buy?userId=${userId}`);
      alert("Purchase created with ID: " + response.data.id);
    } catch (error) {
      alert("Error creating purchase");
    }
  };

  const handleGetPurchases = async () => {
    try {
      const response = await axios.get(`/api/purchases/user/${userId}`);
      setPurchases(response.data);
    } catch (error) {
      alert("Error fetching purchases");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Purchases App</h1>
      <input
        type="text"
        value={userId}
        onChange={(e) => setUserId(e.target.value)}
        placeholder="Enter User ID"
      />
      <div style={{ marginTop: "10px" }}>
        <button onClick={handleBuy}>Buy Now</button>
        <button onClick={handleGetPurchases} style={{ marginLeft: "10px" }}>
          View Purchases
        </button>
      </div>
      <ul>
        {purchases.map((p: any) => (
          <li key={p.id}>
            {p.product?.name} - Qty: {p.quantity} - Total: {p.totalAmount}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
